jQuery('#caas-form').submit(function(e){	    e.preventDefault();		var data = jQuery('#caas-form').serialize();		$('#caas-form').hide();		$('.caas-iframe').show();		$('.caas-iframe').attr('src', '<?=site_url('caas')?>?'+data);			    $("input[name$='address']").val("");    	$("input[name$='f_name']").val("");    	$("input[name$='l_name']").val("");    	$("input[name$='city']").val("");    	$("input[name$='state']").val("");    	$("input[name$='zip']").val("");    	$("input[name$='phone']").val("");    	$("input[name$='email']").val("");				});	jQuery('#certificates-form').submit(function(e){	    e.preventDefault();		var data = jQuery('#certificates-form').serialize();		$('#certificates-form').hide();		$('.certificates-iframe').show();		$('.certificates-iframe').attr('src', '<?=site_url('search')?>?'+data);		    	$("input[name$='f_name']").val("");    	$("input[name$='l_name']").val("");    	$("input[name$='city']").val("");    	$("input[name$='state']").val("");    	$("input[name$='zip']").val("");    	$("input[name$='employer']").val("");    		});	jQuery('#employers-form').submit(function(e){	    e.preventDefault();		var data = jQuery('#employers-form').serialize();		$('#employers-form').hide();		$('.employers-iframe').show();		$('.employers-iframe').attr('src', '<?=site_url('employers')?>?'+data);	    	    $("input[name$='name']").val("");	    $("input[name$='address']").val("");    	$("input[name$='city']").val("");    	$("input[name$='state']").val("");    	$("input[name$='zip']").val("");    	$("input[name$='phone']").val("");    	$("input[name$='email']").val("");    		});	jQuery('#programs-form').submit(function(e){	    e.preventDefault();		var data = jQuery('#programs-form').serialize();		$('#programs-form').hide();		$('.programs-iframe').show();		$('.programs-iframe').attr('src', '<?=site_url('programs')?>?'+data);    		});});function name_field(){		$("input[name$='address']").val("");	$("input[name$='city']").val("");	$("input[name$='state']").val("");	$("input[name$='zip']").val("");	}function position_field(){		$("input[name$='name']").val("");	$("input[name$='f_name']").val("");	$("input[name$='l_name']").val("");	$("input[name$='phone']").val("");	$("input[name$='email']").val("");	$("input[name$='employer']").val("");	}</script><script src="assets/frontends/plugins/greensock/TweenMax.min.js"></script><script src="assets/frontends/plugins/greensock/TimelineMax.min.js"></script><script src="assets/frontends/plugins/scrollmagic/ScrollMagic.min.js"></script><script src="assets/frontends/plugins/greensock/animation.gsap.min.js"></script><script src="assets/frontends/plugins/greensock/ScrollToPlugin.min.js"></script><script src="assets/frontends/plugins/OwlCarousel2-2.2.1/owl.carousel.js"></script><script src="assets/frontends/plugins/easing/easing.js"></script><script src="assets/frontends/plugins/parallax-js-master/parallax.min.js"></script><script src="assets/frontends/js/custom.js"></script></body></html>