ersion = nAgt.substring(nAgt.indexOf('rv:') + 3);    }    // Other browsers    else if ((nameOffset = nAgt.lastIndexOf(' ') + 1) < (verOffset = nAgt.lastIndexOf('/'))) {      browser = nAgt.substring(nameOffset, verOffset);      version = nAgt.substring(verOffset + 1);      if (browser.toLowerCase() == browser.toUpperCase()) {        browser = navigator.appName;      }    }    // trim the version string    if ((ix = version.indexOf(';')) != -1) version = version.substring(0, ix);    if ((ix = version.indexOf(' ')) != -1) version = version.substring(0, ix);    if ((ix = version.indexOf(')')) != -1) version = version.substring(0, ix);    majorVersion = parseInt('' + version, 10);    if (isNaN(majorVersion)) {      version = '' + parseFloat(navigator.appVersion);      majorVersion = parseInt(navigator.appVersion, 10);    }    // mobile version    var mobile = /Mobile|mini|Fennec|Android|iP(ad|od|hone)/.test(nVer);    // cookie    var cookieEnabled = (navigator.cookieEnabled) ? true : false;    if (typeof navigator.cookieEnabled == 'undefined' && !cookieEnabled) {      document.cookie = 'testcookie';      cookieEnabled = (document.cookie.indexOf('testcookie') != -1) ? true : false;    }    // system    var os = unknown;    var clientStrings = [      {s: 'Windows 10', r: /(Windows 10.0|Windows NT 10.0)/},      {s: 'Windows 8.1', r: /(Windows 8.1|Windows NT 6.3)/},      {s: 'Windows 8', r: /(Windows 8|Windows NT 6.2)/},      {s: 'Windows 7', r: /(Windows 7|Windows NT 6.1)/},      {s: 'Windows Vista', r: /Windows NT 6.0/},      {s: 'Windows Server 2003', r: /Windows NT 5.2/},      {s: 'Windows XP', r: /(Windows NT 5.1|Windows XP)/},      {s: 'Windows 2000', r: /(Windows NT 5.0|Windows 2000)/},      {s: 'Windows ME', r: /(Win 9x 4.90|Windows ME)/},      {s: 'Windows 98', r: /(Windows 98|Win98)/},      {s: 'Windows 95', r: /(Windows 95|Win95|Windows_95)/},      {s: 'Windows NT 4.0', r