<?phpclass EmployersObject{  // database connection and table name  private $conn;  private $table_employers = "final_employment_info";  public function __construct()  {    global $db;	    $this->conn = $db;	  }  public function readAllEmployers()  {    $query = "SELECT * FROM " . $this->table_employers . " GROUP BY employer_name";    $stmt = $this->conn->getData($query);    return $stmt;  }  public function countCaas($name)  {    $query = "SELECT count(*) FROM " . $this->table_employers . " WHERE employer_name = '". $name ."'";    $stmt = $this->conn->getData($query);    return $stmt[0]['count(*)'];  }  }