<?phpclass ProgramsObject{  // database connection and table name  private $conn;  private $table_university = "tbl_university";  private $table_PD = "tbl_program_director";  private $table_class = "tbl_class";  private $table_dates = "tbl_expected_dates";  public function __construct()  {    global $db;	    $this->conn = $db;  }  public function readAllUniversity()  {    $query = "SELECT * FROM " . $this->table_university . " ORDER BY id";    $stmt = $this->conn->getData($query);    return $stmt;  }       public function getDirector($id) {    $query = "SELECT * FROM " . $this->table_PD . " WHERE Title = 'Program Director' AND University_Id = ".$id;    $stmt = $this->conn->getData($query);	$full_name = $stmt[0]['First_Name']." ".$stmt[0]['Last_Name'];    return $full_name;  }    public function getCountStudents($id, $year) {    $query = "SELECT count(*) FROM " . $this->table_class . " WHERE class_of = '". $year ."' AND University_id = ".$id;	    $stmt = $this->conn->getData($query);	    return $stmt[0]['count(*)'];      }    public function getExpectedDates($id, $type) {	  	$year = date('Y');    $query = "SELECT * FROM " . $this->table_dates . " WHERE class_of_year = '". $year ."' AND University_Id = ".$id;	    $stmt = $this->conn->getData($query);		if($type == "cert"){				if($stmt = $this->conn->getData($query)){						if(empty($stmt[0]['Expected_Cert_Exam']) == false){								return $stmt[0]['Expected_Cert_Exam'];							}else{								return "N/A";							}					}else{						return "N/A";					}			}else{				if($stmt = $this->conn->getData($query)){						if(empty($stmt[0]['Expected_Graduation']) == false){								return $stmt[0]['Expected_Graduation'];							}else{								return "N/A";							}					}else{						return "N/A";					}			}  }  }