<?phpclass SettingsObject{  // database connection and table name  private $conn;  private $table_settings = "setting";  public function __construct()  {    global $db;	    $this->conn = $db;	  }  public function readAllSettings()  {    $query = "SELECT * FROM " . $this->table_settings . " ORDER BY field";    $stmt = $this->conn->getData($query);    return $stmt;  }  }