<?phpclass VisitorsObject{  // database connection and table name  private $conn;  private $table_visitors = "visitors";  public function __construct()  {    global $db;    //$db = Database::getInstance();    $this->conn = $db;    //$db = new Database;    //exit(0);  }  // read all surveys  public function readAllVisitors()  {	      $query = "SELECT * FROM " . $this->table_visitors . " ORDER BY id DESC";		$result = $this->conn->getQuery($query);		$rows = array();		while ($row = $result->fetch_array()) {					array_push($rows, [$row['user_id'], $row['full_name'], $row['role'], $row['email'], $row['created_at'], $row['browser'], $row['ip'], $row['os']]);				}    		return $rows;    }  }